class Solution:
    def longestPalindrome(self, s: str) -> str:
        longest_palindrome, lenth_longest_palindrome = s[0], 1
        for i in range(len(s)):
            for j in range(i+1, len(s)+1):
                l1 = s[i:j]
                l2 = l1[::-1]
                if palindromeTest(l1, l2) and len(l1) > lenth_longest_palindrome:
                    longest_palindrome, lenth_longest_palindrome = "".join(
                        l1), len(l1)
            if lenth_longest_palindrome == len(s[i:]):
                return longest_palindrome

        return longest_palindrome


def palindromeTest(l1, l2):
    for i in range(len(l1)):
        if l1[i] != l2[i]:
            return False
    return True


